.LSPVars:	dc.l	0			; 0  word stream loop
			dc.w	0			; 4  reloc has been done
			dc.w	0			; 6  current music BPM
			dc.l	0			; 8  byte stream
			dc.l	0			; 12 m_lfmDmaConPatch
			dc.l	0			; 16 word stream
			dc.l	0			; 20 word stream loop
			dc.l	0			; 24 byte stream loop

; WARNING: in word stream, instrument offset is shifted by -12 bytes (3 last long of .LSPVars)
.LSP_InstrumentInfo:			; (7 instruments)
			dc.w	$0000,$0004,$0141,$0000,$0006,$0040
			dc.w	$0000,$0286,$0101,$0000,$0288,$0040
			dc.w	$0000,$0488,$04a4,$0000,$0488,$0001
			dc.w	$0000,$0dd0,$01b1,$0000,$0dd0,$0001
			dc.w	$0000,$1132,$0114,$0000,$12e2,$0004
			dc.w	$0000,$15dc,$00a5,$0000,$15de,$0004
			dc.w	$0000,$135a,$0141,$0000,$135c,$0010

.resetv:	dc.l	0,0,0,0

; a0: music data (any mem)
; a1: sound bank data (chip mem)
; a2: 8bit DMACON byte address
.LSP_PlayerInit:
			move.l	#$6451ca23,d0
			cmp.l	(a1),d0
			bne.s	.dataError
			cmpi.l	#'LSP1',(a0)+
			bne.s	.dataError
			cmp.l	(a0)+,d0
			bne.s	.dataError
			lea		368(a0),a0		; skip header
			lea		.LSPVars(pc),a3
			move.l	a2,12(a3)
			move.l	a0,16(a3)		; word stream ptr
			movea.l	a0,a4
			add.l	#24390,a4
			move.l	a4,8(a3)		; byte stream ptr

			move.l	a0,(a3)	; word stream loop ptr
			move.l	a4,24(a3)	; byte stream loop ptr
			tst.b	4(a3)
			bne.s	.noReloc
			st		4(a3)
			moveq	#7-1,d0
			lea		.LSP_InstrumentInfo(pc),a0
			move.l	a1,d1
.rloop:		add.l	d1,(a0)
			add.l	d1,6(a0)
			lea		12(a0),a0
			dbf		d0,.rloop
.noReloc:	bset.b	#1,$bfe001	; disable this fucking Low pass filter!!
			lea		.LSPVars+6(pc),a0
			move.w	#125,(a0)		; music BPM
			rts

