.resetv:	dc.l	0,0,0,0

; a0: music data (any mem)
; a1: sound bank data (chip mem)
; a2: 8bit DMACON byte address
.LSP_PlayerInit:
			move.l	#$f44581fd,d0
			cmp.l	(a1),d0
			bne.s	.dataError
			cmpi.l	#'LSP1',(a0)+
			bne.s	.dataError
			cmp.l	(a0)+,d0
			bne.s	.dataError
			lea		688(a0),a0		; skip header
			lea		.LSPVars(pc),a3
			move.l	a2,12(a3)
			move.l	a0,16(a3)		; word stream ptr
			movea.l	a0,a4
			add.l	#16552,a4
			move.l	a4,8(a3)		; byte stream ptr

			add.l	#5126,a0	; word stream loop pos
			move.l	a0,(a3)	; word stream loop ptr
			add.l	#7556,a4	; byte stream loop pos
			move.l	a4,24(a3)	; byte stream loop ptr
			tst.b	4(a3)
			bne.s	.noReloc
			st		4(a3)
			moveq	#16-1,d0
			lea		.LSP_InstrumentInfo(pc),a0
			move.l	a1,d1
.rloop:		add.l	d1,(a0)
			add.l	d1,6(a0)
			lea		12(a0),a0
			dbf		d0,.rloop
.noReloc:	bset.b	#1,$bfe001	; disable this fucking Low pass filter!!
			lea		.LSPVars+6(pc),a0
			move.w	#125,(a0)		; music BPM
			rts

.dataError:	illegal
; 236 specific callback
.r_None:
		move.l	a0,(a1)+
		rts

.r_Av:
		move.b	(a0)+,$09(a6)
		move.l	a0,(a1)+
		rts

.r_Dv:
		move.b	(a0)+,$39(a6)
		move.l	a0,(a1)+
		rts

.r_ArBrDr:
		move.l	a0,(a1)+
		lea		.resetv(pc),a3
		move.l	(a3)+,a4
		move.l	(a4)+,$30(a6)
		move.w	(a4)+,$34(a6)
		move.l	4(a3),a4
		move.l	(a4)+,$10(a6)
		move.w	(a4)+,$14(a6)
		move.l	8(a3),a4
		move.l	(a4)+,(a6)
		move.w	(a4)+,$04(a6)
		rts

.r_Cv:
		move.b	(a0)+,$29(a6)
		move.l	a0,(a1)+
		rts

.r_CvDv:
		move.b	(a0)+,$39(a6)
